package ch.bbw.zork;/** * Class Command - Part of the "Zork" game. * Author: Tobias Bertschi, Yves Huber */public class Command {    private String commandWord;    private String secondWord;    public Command(String commandWord) {        this(commandWord, null);    }    public Command(String commandWord, String secondWord) {        this.commandWord = commandWord;        this.secondWord = secondWord;    }    public String getCommandWord() {        return commandWord;    }    public String getSecondWord() {        return secondWord;    }    public boolean isUnknown() {        return (commandWord == null);    }    public boolean hasSecondWord() {        return (secondWord != null);    }}