package ch.bbw.zork;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.stream.Collectors;/** * Author: Tobias Bertschi, Yves Huber */public class Room {    private String description;    private HashMap<String, Room> exits;    private Item item;    private Riddle riddle;    private boolean locked;    private Item key;    public Room(String description) {        this.description = description;        this.exits = new HashMap<>();    }    public Room(String description, Item item) {        this.description = description;        this.exits = new HashMap<>();        this.item = item;    }    public Room(String description, Item item, Riddle riddle) {        this.description = description;        this.exits = new HashMap<>();        this.item = item;        this.riddle = riddle;    }    public Room(String description, Item item, Riddle riddle, boolean locked, Item key) {        this.description = description;        this.exits = new HashMap<>();        this.item = item;        this.riddle = riddle;        this.locked = locked;        this.key = key;    }    public void setExits(Room north, Room east, Room south, Room west) {        exits.put("north", north);        exits.put("east", east);        exits.put("south", south);        exits.put("west", west);    }    public String shortDescription() {        return description;    }    public String longDescription() {        StringBuilder stringBuilder = new StringBuilder("You are in " + description + ".\n");        stringBuilder.append(exitString());        return stringBuilder.toString();    }    private String exitString() {        return "Exits: " + String.join(" ", exits.keySet());    }    public Room nextRoom(String direction) {        return exits.get(direction);    }    public boolean unlock(ArrayList<Item> backpack) {        for (Item item : backpack) {            if (item.getId() == key.getId()) {                return true;            }        }        return false;    }    public Item getItem() {        return item;    }    public void setItem(Item item) {        this.item = item;    }    public boolean startRiddle() {        return riddle.start();    }    public Riddle getRiddle() {        return riddle;    }    public void setRiddle(Riddle riddle) {        this.riddle = riddle;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public boolean isLocked() {        return locked;    }    public void setLocked(boolean locked) {        this.locked = locked;    }    public Item getKey() {        return key;    }    public void setKey(Item key) {        this.key = key;    }}